#This Function allows you to extract each elemnt from list
#specifically for nested list
def print_elist(a_list):
  for eachelement in  a_list:
    #checks whether elemnt is list or not
    if isinstance(eachelement,list):
      #if its list retrive the function again
      print_elist(eachelement)
    else:
      #If its not list , we will print element
      print(eachelement)